import React from 'react'
import style from '../Card/Card.module.css'
import { Link } from 'react-router-dom'

const Card = ({ dog, temperaments }) => { // Agrega temperaments como prop
    const { id, image, name, temperament, weight } = dog;

    const temperamentNames = temperament.split(','); // Convierte la cadena en una matriz

    const temperamentList = temperamentNames.map((tempName, index) => (
        <span key={index} className={style.temperament}>
            {tempName.trim()}
        </span>
    ));

    return (
        <div className={style.card}>
            <Link to={`/detail/${id}`} className={style.link}>
                <img src={image} alt='A dog' />
                <h1>{name}</h1>
                <div className={style.temperamentList}>{temperamentList}</div> {/* Muestra los temperamentos */}
                <p>{weight} kg</p>
            </Link>
        </div>
    )
}
export default Card;
